import React,  { useState, useEffect } from 'react';
import axios from 'axios';
import ReactPaginate from 'react-paginate';

const CharacterItems = ({item}) => {
  // console.log(item.name)
    const [quote, setQuote] = useState();


    const fetchItems = async () => {
      const result = await axios(
      `https://www.breakingbadapi.com/api/quote?author=${item.name}`
    )

    setQuote(result.data);
    // console.log(result.data+item)


    }

    useEffect(() => {

    fetchItems();  
  }, [])

  useEffect (() => {
    console.log(quote)
  }, [quote] 
  )



    return (
    <div className='card'>
      <div className='card-inner'>
        <div className='card-front'>
          <img src={items.img} alt='' />
        </div>
        <div className='card-back'>
          <h1>{items.name}</h1>
          <ul>
            <li>
              <strong>Name:</strong> {items.name}
            </li>
            <li>
              <strong>Date of Birthday:</strong> {items.birthday}
            </li>
            <li>
              <strong>Occupation:</strong> {items.occupation}
            </li>
            <li>
              <strong>Status:</strong> {items.status}
            </li>
            <li>
              <strong>Nickname:</strong> {items.nickname}
            </li>
            <li>
              <strong>Portrays Actor:</strong> {items.portrayed}
            </li>
            <li>
              <strong>Seasons:</strong> {items.appearance}
            </li>
            <li>
              <strong>Quotes:</strong> {quote && quote.map((val) => {
                return (
                  <p>{val.quote}</p>
                );
              } )}
            </li>
          </ul>
        </div>
      </div>
    </div>
    );



    return (
      <>
      {display}
      <ReactPaginate 
        previousLabel = {"Prevoius"}
        nextLabel={"Next"}
        pageCount={pageCount}
        onPageChange={changePage}
      />
</>
    );
}

export default CharacterItems;
