import React,  { useState, useEffect } from 'react';
import axios from 'axios';

const CharacterItems = ({item}) => {
    const [quote, setQuote] = useState([]);
    useEffect(() => {
        const fetchItems = async () => {
        const result = await axios(
        `https://www.breakingbadapi.com/api/quote?author=$Skyler+White`
      )
    setQuote(result.data);
    console.log(result.data.quote)
    }
    fetchItems();
  }, [item.name])


    return (
        <div className='card'>
      <div className='card-inner'>
        <div className='card-front'>
          <img src={item.img} alt='' />
        </div>
        <div className='card-back'>
          <h1>{item.name}</h1>
          <ul>
            <li>
              <strong>Name:</strong> {item.name}
            </li>
            <li>
              <strong>Date of Birthday:</strong> {item.birthday}
            </li>
            <li>
              <strong>Occupation:</strong> {item.occupation}
            </li>
            <li>
              <strong>Status:</strong> {item.status}
            </li>
            <li>
              <strong>Nickname:</strong> {item.nickname}
            </li>
            <li>
              <strong>Portrays Actor:</strong> {item.portrayed}
            </li>
            <li>
              <strong>Seasons:</strong> {item.appearance}
            </li>
            <li>
              <strong>Quotes:</strong> {quote.quote}
            </li>
          </ul>
        </div>
      </div>
    </div>
    );
}

export default CharacterItems;
